import axios from 'axios';
import React from 'react'
import { useState, useEffect } from "react";
export default function Login() {
    const [user, setUser] = useState({
        name: '',
        email: '',
        password: ''
    })
    const [err,setErr]= useState('') 
    const onChangeInput = e => {
        const { name, value } = e.target;
        setUser({ ...user, [name]: value })
        setErr('')
    }
    const registerSubmit = async e => {
        e.preventDefault()
        try {
            const res = await axios.post('/users/register', {
                username: user.name,
                email: user.email,
                password:user.password

            })
            setUser({
                name: '',
                email: '',
                password:''
            })
            setErr
        } catch (error) {
            error.response.data.msg && setErr(err.response.data.msg)
        }
    }


    return (
      <section>
        <div className="login">
          <h2>Login</h2>
          <form>
            <input
              type="email"
              name="email"
              id="login-email"
              placeholder="Email"
              required
              value={user.email}
              onChange={onChangeInput}
            />
            <input
              type="password"
              name="password"
              id="login-password"
              placeholder="Password"
              required
              value={user.password}
              autoComplete="true"
              onChange={onChangeInput}
            />
            <button type="submit">Login</button>
            <span>Login Now</span>
            <h3>{err}</h3>
          </form>
        </div>
        <div>
          <div className="Register">
            <h2>Register</h2>
                    <form onSubmit={ registerSubmit}>
              <input
                type="text"
                name="name"
                id="register-name"
                placeholder="User Name"
                required
                value={user.name}
                onChange={onChangeInput}
              />

              <input
                type="email"
                name="email"
                id="register-email"
                placeholder="Email"
                required
                value={user.email}
                onChange={onChangeInput}
              />
              <input
                type="password"
                name="password"
                id="register-password"
                placeholder="Password"
                required
                value={user.password}
                autoComplete="true"
                onChange={onChangeInput}
              />
              <button type="submit">Register</button>
              <span>Register Now</span>
              <h3>{err}</h3>
            </form>
          </div>
        </div>
      </section>
    );
}
